#!/bin/bash

# Web Automation Recorder Icon Generator
# Creates simple but proper PNG icons for the extension

# 16x16 icon (base64 encoded PNG with record/play symbols)
echo "" | base64 -d > icon-16.png

# 32x32 icon
echo "" | base64 -d > icon-32.png

# 48x48 icon
echo "" | base64 -d > icon-48.png

# 128x128 icon
echo "" | base64 -d > icon-128.png

# Create recording state icons (with red background)
echo "" | base64 -d > icon-recording-16.png

echo "" | base64 -d > icon-recording-32.png

echo "" | base64 -d > icon-recording-48.png

echo "" | base64 -d > icon-recording-128.png

echo "Icon files generated successfully!"